import { useEffect } from 'react';
import { useState } from 'react';
import Card from '../components/Card';

const Photos = () => {
  const [photos, setPhotos] = useState([]);
  const [sort, setSort] = useState('asc');
  const [submited, setSubmited] = useState('');
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const deletePhoto = (id) => {
    fetch(`https://gallery-app-server.vercel.app/photos/${id}`, {
      method: 'DELETE',
    }).then(() => {
      setPhotos(photos.filter((photo) => photo.id !== id));
    });
    // TODO: answer here
  };

  useEffect(() => {
    let url = '';
    if (sort === 'asc') {
      return (url = `https://gallery-app-server.vercel.app/photos?_sort=id&_order=asc&q=${search}`);
    } else if (sort === 'desc') {
      return (url = `https://gallery-app-server.vercel.app/photos?_sort=id&_order=desc&q=${search}`);
    }
    fetch(url)
      .then((resp) => resp.json())
      .then((json) => {
        setPhotos(json);
        setLoading(false);
        setError(false);
      })
      .catch(() => {
        setLoading(true);
        setError(true);
      });
  }, [search, sort, submited]);

  useEffect(() => {
    fetch('https://gallery-app-server.vercel.app/photos')
      .then((resp) => resp.json())
      .then((json) => {
        setPhotos(json);
        setLoading(false);
      })
      .catch(() => {
        setLoading(true);
        setError(false);
      });
    // TODO: answer here
  }, []);

  if (error) return <h1 style={{ width: '100%', textAlign: 'center', marginTop: '20px' }}>Error!</h1>;

  return (
    <>
      <div className="container">
        <div className="options">
          <select onChange={(e) => setSort(e.target.value)} data-testid="sort" className="form-select" style={{}}>
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              setSubmited(search);
            }}
          >
            <input type="text" data-testid="search" onChange={(e) => setSearch(e.target.value)} className="form-input" />
            <input type="submit" value="Search" data-testid="submit" className="form-btn" />
          </form>
        </div>
        <div className="content">
          {loading ? (
            <h1 style={{ width: '100%', textAlign: 'center', marginTop: '20px' }}>Loading...</h1>
          ) : (
            photos.map((photo) => {
              return <Card key={photo.id} photo={photo} deletePhoto={deletePhoto} />;
            })
          )}
        </div>
      </div>
    </>
  );
};

export default Photos;
